import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from "react-router-dom";

import AboutUsPage from "./pages/AboutUs.js";
import Home from "./pages/HomeDesign.js";
import SkillsAndExperience from "./pages/SkillsSection.js";
import Navbar from "./pages/Navbar.js";
import Portfolio from "./pages/Portfolio.js";
import ContactSection from "./pages/ContactUs.js";
import ProgressBar from "./components/ProgressBar"; // Import the ProgressBar component

const AppRouter = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [targetRoute, setTargetRoute] = useState(null);

  const handleNavigation = (path) => {
    setLoading(true);
    setTargetRoute(path);
    setTimeout(() => {
      setLoading(false);
      navigate(path);
    }, 3000); // 3-second delay before navigation
  };

  return (
    <>
      <Navbar />
      {loading && <ProgressBar />}
      {!loading && (
        <Routes>
          <Route path="/" exact element={<Home navigateTo={handleNavigation} />} />
          <Route path="/home" exact element={<Home navigateTo={handleNavigation} />} />
          <Route path="/aboutus" exact element={<AboutUsPage navigateTo={handleNavigation} />} />
          <Route path="/skills" exact element={<SkillsAndExperience navigateTo={handleNavigation} />} />
          <Route path="/portfolio" exact element={<Portfolio navigateTo={handleNavigation} />} />
          <Route path="/contact" exact element={<ContactSection navigateTo={handleNavigation} />} />
        </Routes>
      )}
    </>
  );
};

export default AppRouter;
